import React, { useState } from 'react';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [passToggle, setPassToggle] = useState(false);
  const [validation, setValidation] = useState(false);
  const [data, setData] = useState({ username: "", email: "", password: "", password2: "" });
  const [logindata, setLoginData] = useState({ email: "", password: "" });
  const [arr, setArr] = useState([]);

  const handleChange = (e) => {
    let { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const handleSignup = (e) => {
    e.preventDefault();
    if (data.password === data.password2) {
      setArr([...arr, data]);
      setIsLogin(false);
    } else {
      alert("Please enter the same password");
    }
  };

  const handleLoginChange = (e) => {
    let { name, value } = e.target;
    setLoginData({ ...logindata, [name]: value });
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const userExists = arr.filter((el) => el.email === logindata.email && el.password === logindata.password);

    if (userExists.length > 0) {
      alert("Login success");
    } else {
      alert("Login failed");
    }
  };

  const handleBlur = (e) => {
    let { name, value } = e.target;
    let emailValid = /^[a-zA-Z0-9.!#$%&'+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)$/.test(data.email);
    


    if (emailValid.test(data.email)) {
      setValidation(false);
    }
    if (name === 'password' || name === 'password2') {
      setValidation({ ...validation, password: !passwordValid });
    }
  };

  return (
    <>
      {isLogin ? (
        <div id="login-box">
          <div className="left-box">
            <h1> Sign Up </h1>
            <form onSubmit={handleSignup}>
              <input type="text" name="username" onChange={handleChange} placeholder="Username" onBlur={handleBlur} />
              {validation.username && <p style={{ color: "red" }}>Please enter a valid username</p>}

              <input type="text" name="email" onChange={handleChange} placeholder="Email" onBlur={handleBlur} required />
              {validation.email && <p style={{ color: "red" }}>Please enter a valid email</p>}

              <input type={passToggle ? 'text' : "password"} name="password" onChange={handleChange} placeholder="Password" onBlur={handleBlur} required />
              {validation.password && <p style={{ color: "red" }}>Please enter a valid password</p>}

              <input type={passToggle ? 'text' : "password"} name="password2" onChange={handleChange} placeholder="Retype Password" onBlur={handleBlur} />
              {validation.password2 && <p style={{ color: "red" }}>Please enter a valid password</p>}

              <>
                {passToggle ? (
                  <>
                    <span onClick={() => setPassToggle(false)} className="material-symbols-outlined">visibility</span>Hide Password
                  </>
                ) : (
                  <>
                    <span onClick={() => setPassToggle(true)} className="material-symbols-outlined">visibility_off</span>Show Password
                  </>
                )}
              </>
              <input type="submit" name="signup-button" placeholder="Sign Up" />
              <span onClick={() => setIsLogin(false)}>Login</span>
            </form>
          </div>
          <div className="right-box">
            <span className="signinwith">Sign in with<br />Social Network</span>
            <button className="social facebook">Log in with Facebook</button>
            <button className="social twitter">Log in with Twitter</button>
            <button className="social google">Log in with Google+</button>
          </div>
          <div className="or">OR</div>
        </div>
      ) : (
        <div id="login-box">
          <div className="left-box">
            <h1> Login </h1>
            <form onSubmit={handleLogin}>
              <input type="text" name="email" placeholder="Email" value={logindata.email} onChange={handleLoginChange} required />
              <input type="password" name="password" value={logindata.password} placeholder="Password" onChange={handleLoginChange} required />
              <input type="submit" name="signup-button" placeholder="Sign Up" />
              <span onClick={() => setIsLogin(true)}>Signup</span>
            </form>
          </div>
          <div className="right-box">
            <span className="signinwith">Sign in with<br />Social Network</span>
            <button className="social facebook">Log in with Facebook</button>
            <button className="social twitter">Log in with Twitter</button>
            <button className="social google">Log in with Google+</button>
          </div>
          <div className="or">OR</div>
        </div>
      )}
    </>
  );
};

export default Login;
