import React, { useState } from 'react';

const Login = () => {
  const [login, setLogin] = useState(true);
  const [passToggle, setPassToggle] = useState(false);
  const [validation, setValidation] = useState({});
  const [data, setData] = useState({
    username: "",
    email: "",
    password: "",
    password2: ""
  });
  const [logindata, setLoginData] = useState({
    email: "",
    password: ""
  });
  const [arr, setArr] = useState([]);

  const handleChange = (e) => {
    let { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const handleSignup = (e) => {
    e.preventDefault();
    if (validateSignup()) {
      setArr([...arr, data]);
      alert("Signup success!");
      setLogin(true);
    } else {
      alert("Please fix the errors in the form.");
    }
  };

  const validateSignup = () => {
    const errors = {};
    const validEmail = /^[a-zA-Z0-9.!#$%&'+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;
    const validUsername = /^[0-9A-Za-z]{6,16}$/;
    const validPassword = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@.#$!%?&^])[A-Za-z\d@.#$!%?&]{8,15}$/;

    if (!validEmail.test(data.email)) {
      errors.email = "Please enter a valid email.";
    }
    if (!validUsername.test(data.username)) {
      errors.username = "Username must be 6-16 characters long and can only contain letters and numbers.";
    }
    if (!validPassword.test(data.password)) {
      errors.password = "Password must be 8-15 characters long and include uppercase, lowercase, number, and special character.";
    }
    if (data.password !== data.password2) {
      errors.password2 = "Passwords do not match.";
    }

    setValidation(errors);
    return Object.keys(errors).length === 0;
  };

  const handleLoginDataChange = (e) => {
    let { name, value } = e.target;
    setLoginData({ ...logindata, [name]: value });
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const userExists = arr.find(
      (el) => el.email === logindata.email && el.password === logindata.password
    );

    if (userExists) {
      alert("Login success");
    } else {
      alert("Login failed");
    }
  };

  return (
    <>
      {login ? (
        <div id="login-box">
          <div className="left-box">
            <h1>Sign Up</h1>
            <form onSubmit={handleSignup}>
              <input
                type="text"
                name="username"
                onChange={handleChange}
                placeholder="Username"
                onFocus={validateSignup}
                required
              />
              {validation.username && <p style={{ color: "red" }}>{validation.username}</p>}
              <input
                type="text"
                name="email"
                onChange={handleChange}
                placeholder="Email"
                onFocus={validateSignup}
                required
              />
              {validation.email && <p style={{ color: "red" }}>{validation.email}</p>}
              <input
                type={passToggle ? 'text' : "password"}
                name="password"
                onChange={handleChange}
                placeholder="Password"
                onFocus={validateSignup}
                required
              />
              {validation.password && <p style={{ color: "red" }}>{validation.password}</p>}
              <input
                type={passToggle ? 'text' : "password"}
                name="password2"
                onChange={handleChange}
                placeholder="Retype Password"
                onFocus={validateSignup}
                required
              />
              {validation.password2 && <p style={{ color: "red" }}>{validation.password2}</p>}
              <span onClick={() => setPassToggle(!passToggle)} className="material-symbols-outlined">
                {passToggle ? 'visibility' : 'visibility_off'}
              </span>
              <input type="submit" value="Sign Up" />
              <span onClick={() => setLogin(false)}>Login</span>
            </form>
          </div>
          <div className="right-box">
            <span className="signinwith">Sign in with<br />Social Network</span>
            <button className="social facebook">Log in with Facebook</button>
            <button className="social twitter">Log in with Twitter</button>
            <button className="social google">Log in with Google+</button>
          </div>
          <div className="or">OR</div>
        </div>
      ) : (
        <div id="login-box">
          <div className="left-box">
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
              <input
                type="text"
                name="email"
                placeholder="Email"
                value={logindata.email}
                onChange={handleLoginDataChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={logindata.password}
                onChange={handleLoginDataChange}
                required
              />
              <input type="submit" value="Login" />
              <span onClick={() => setLogin(true)}>Signup</span>
            </form>
          </div>
          <div className="right-box">
            <span className="signinwith">Sign in with<br />Social Network</span>
            <button className="social facebook">Log in with Facebook</button>
            <button className="social twitter">Log in with Twitter</button>
            <button className="social google">Log in with Google+</button>
          </div>
          <div className="or">OR</div>
        </div>
      )}
    </>
  );
};

export default Login;
